  --sidebar-accent-foreground: hsl(355 91% 65%);
  --sidebar-border: hsl(214.3 31.8% 91.4%);
  --sidebar-ring: hsl(355 91% 65%);
  --font-sans: Inter, system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
}

.dark {
  --background: hsl(0 0% 0%);
  --foreground: hsl(200 6.6667% 91.1765%);
  --card: hsl(228 9.8039% 10%);
  --card-foreground: hsl(0 0% 85.0980%);
  --popover: hsl(0 0% 0%);
  --popover-foreground: hsl(200 6.6667% 91.1765%);
  --primary: hsl(355 91% 65%);
  --primary-foreground: hsl(210 40% 98%);
  --secondary: hsl(195.0000 15.3846% 94.9020%);
  --secondary-foreground: hsl(210 25% 7.8431%);
  --muted: hsl(0 0% 9.4118%);
  --muted-foreground: hsl(210 3.3898% 46.2745%);
  --accent: hsl(205.7143 70% 7.8431%);
  --accent-foreground: hsl(355 91% 45%);
  --destructive: hsl(0 84.2% 60.2%);
  --destructive-foreground: hsl(210 40% 98%);
  --border: hsl(210 5.2632% 14.9020%);
  --input: hsl(207.6923 27.6596% 18.4314%);
  --ring: hsl(355 91% 65%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer components {
  .animate-fade-in {
    animation: fadeIn 0.8s ease-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  .card-hover {
    transition: all 0.3s ease;
    transform-style: preserve-3d;
  }

  .card-hover:hover {
    transform: translateY(-8px) rotateX(5deg);
    box-shadow: 0 20px 40px rgba(255, 87, 87, 0.15);
  }

  .logo-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
  }

  .product-zoom {
    transition: transform 0.3s ease;
  }

  .product-zoom:hover {
    transform: scale(1.05);
  }

  .category-card {
    background: linear-gradient(135deg, var(--background) 0%, var(--muted) 100%);
  }

  .category-card img {
    aspect-ratio: 9/16;
    object-fit: cover;
  }

  .hero-gradient {
    background: linear-gradient(135deg, hsl(355 91% 65%) 0%, hsl(355 91% 55%) 100%);
  }

  /* Breaking News Marquee Animation */
  .animate-marquee {
    animation: marquee 20s linear infinite;
  }

  @keyframes marquee {
    0% { transform: translateX(100%); }
    100% { transform: translateX(-100%); }
  }

  .hero-jersey-showcase {
    position: relative;
  }

  .hero-jersey-showcase::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    background: linear-gradient(45deg, var(--primary), transparent, var(--primary));
    border-radius: 20px;
    z-index: -1;
    opacity: 0.3;
    animation: rotate 10s linear infinite;
  }

  @keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  .product-card-animation {
    transform: translateY(0);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .product-card-animation:hover {
    transform: translateY(-12px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }

  .product-card-animation:nth-child(even) {
    animation-delay: 0.1s;
  }

  .product-card-animation:nth-child(3n) {
    animation-delay: 0.2s;
  }

  .product-card-animation:nth-child(4n) {
    animation-delay: 0.3s;
  }

  @keyframes slideInFromBottom {
    0% {
      transform: translateY(30px);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  .product-card-animation {
    animation: slideInFromBottom 0.6s ease-out forwards;
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .sports-category-hover {
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
  }

  .sports-category-hover::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
  }

  .sports-category-hover:hover::before {
    left: 100%;
  }

  .jersey-glow {
    position: relative;
  }

  .jersey-glow::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at center, var(--primary) 0%, transparent 70%);
    opacity: 0;
    transition: opacity 0.3s ease;
    pointer-events: none;
    border-radius: inherit;
  }

  .jersey-glow:hover::after {
    opacity: 0.1;
  }
}

